@js:
let list = [];
try {
    let uesrResult = JSON.parse(result).data.user.result;
    let isV2 = Object.hasOwnProperty.call(uesrResult, "timeline_v2");
    let instructions = uesrResult[isV2 ? "timeline_v2" : "timeline"].timeline.instructions;
    let addList = (item) => {
        let tweet_result = item.itemContent.tweet_results.result;
        let legacy = tweet_result["__typename"] === "TweetWithVisibilityResults" ? tweet_result.tweet.legacy : tweet_result.legacy;
        let media = legacy.entities.media ?? [];
        if (media.length > 0 && media[0].type == "video") {
            list.push({
                "title": legacy.full_text,
                "cover": media[0].media_url_https,
                "detailUrl": JSON.stringify(media[0].video_info.variants),
            });
        }
    }
    for (const it of instructions) {
        switch (it.type) {
            case 'TimelineAddToModule':
                for (const it2 of it.moduleItems) {
                    addList(it2.item);
                }
                break;
            case 'TimelineAddEntries':
                for (const it2 of it.entries) {
                    if (it2.entryId.startsWith("cursor-bottom")) {
                        let bottom = it2.content.value;
                        await tools.setCache("bottom_value", bottom);
                        // console.log("下一页:" + bottom);
                    } else if (it2.entryId.startsWith("profile-grid")) {
                        for (const it3 of it2.content.items) {
                            addList(it3.item);
                        }
                    } else if (it2.entryId.startsWith("cursor-") == false && isV2 == false) {
                        addList(it2.content);
                    } else if (it2.entryId.startsWith("tweet-")) {
                        addList(it2.content);
                    }
                }
                break;
            default:
                break;
        }
    }
} catch (_) {
}
return list;

