function generateUUID() {
    const chars = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz';
    const uuidChars = [];
    // UUID格式中的固定位置字符
    uuidChars[8] = uuidChars[13] = uuidChars[18] = uuidChars[23] = '-';
    uuidChars[14] = '4';
    for (let i = 0; i < 36; i++) {
        if (!uuidChars[i]) {
            let tmp = 0x0 | Math.random() * 0x10;
            let randomCharIndex = i === 19 ? (tmp & 0x3) | 0x8 : tmp & 0xf;
            uuidChars[i] = chars[randomCharIndex];
        }
    }
    return uuidChars.join('');
}
