chapterRule.roads:
@js:
let list = [];
let roads =
    tools.cssSelector(result, '.module-tab-item@data-dropdown-value');
let chapters =
    tools.cssSelector(result, '.module-play-list-content@outerHtml');
for (let index = 0; index < chapters.length; index++) {
    let a1 = tools.cssSelector(chapters[index], 'a@href');
    let a2 = tools.cssSelector(chapters[index], 'a@text');
    let items = [];
    for (let j = 0; j < a1.length; j++) {
        items.push({ 'title': a2[j], 'url': a1[j] });
    }
    list.push({ 'name': roads[index], 'items': items });
}
return list;

