const toByteArray = (wordArray) => {
  let words = wordArray.words;
  let sigBytes = wordArray.sigBytes;
  let byteArray = new Uint8Array(sigBytes);
  for (let i = 0; i < sigBytes; i++) {
    byteArray[i] = (words[i >>> 2] >>> (24 - (i % 4) * 8)) & 0xff;
  }
  return byteArray;
};
