let res = await tools.httpRequest(result.url);
let wordArray = CryptoJS.lib.WordArray.create(new Uint8Array(res.bytes));
let dataBase64 = wordArray.toString(CryptoJS.enc.Base64);
//console.log(dataBase64)
let key = CryptoJS.enc.Utf8.parse(result.key);

let decrypted = CryptoJS.Blowfish.decrypt(dataBase64, key, {
    mode: CryptoJS.mode.ECB, 
    padding: CryptoJS.pad.NoPadding 
}).toString(CryptoJS.enc.Utf8);

return decrypted

