@js:
let { body, headers, statusCode } = params.response;
let reqHeaders = params.request.headers;
if (RegExp("系统安全验证").test(body)) {
    let requestUrl = params.request.url;
    let ck = headers['set-cookie']?.split(";");
    if (ck == null) {
        ck = [reqHeaders["Cookie"]];
    }
    headers = config.httpHeaders ?? {};
    headers["Cookie"] = ck[0];
    headers["Referer"] = requestUrl;
    let verifyUrl = config.host + '/index.php/verify/index.html?';
    verifyUrl = verifyUrl + "," + JSON.stringify(headers);
    let code = await tools.getVerificationCode(verifyUrl);
    verifyUrl = "/index.php/ajax/verify_check?type=search&verify=" + code.code;
    headers["x-requested-with"] = "XMLHttpRequest";
    let resp = await tools.httpRequest({ "url": verifyUrl, "method": "POST", "headers": headers });
    if (JSON.parse(resp.body).code != 1) {
        throw '验证码错误';
    }
    delete headers["x-requested-with"];
    resp = await tools.httpRequest({ "url": requestUrl, "headers": headers });
    result = resp.body;
}
return await tools.cssSelector(result, ".module-search-item");

