function ecb_decode(data) {
    var key = CryptoJS.enc.Utf8.parse('976f97d638360cde');
    let words = CryptoJS.lib.WordArray.create(data);
    var tmp = CryptoJS.AES.decrypt(words.toString(CryptoJS.enc.Utf8), key, { mode: CryptoJS.mode.ECB });
    return tmp.toString(CryptoJS.enc.Utf8);
}
let b = new Uint8Array(coverParam.bytes);
let result = ecb_decode(b)
    .replace(/data:image\/.*?;base64,/g, "");
let decryptData = CryptoJS.enc.Base64.parse(result);
let encryptedUint8Array = new Uint8Array(decryptData.sigBytes);
for (let i = 0; i < decryptData.sigBytes; i++) {
    encryptedUint8Array[i] = decryptData.words[i >>> 2] >>> (24 - (i % 4) * 8) & 0xff;
}

return JSON.stringify({'bytes':Array.from(encryptedUint8Array)});

