function getParams(url, time) {
    
    function md5(data){
        return CryptoJS.MD5(data).toString()
    }

    const e = "asg#w@!aasdgs2345asga-1_s!as==g";
    const api = url.match(/\/api\/apps\/.*?(?=\?|$)/)[0];
    
    return {
        sign: md5(e + time + api),
        Fing: md5(e + time + e),
        token:md5(e + time),
    }
}


function myhttp(url, data) {
    let time = String(parseInt(new Date().getTime()/1000));
    let {sign, Fing, token} = getParams(url, time);
    data["time"] = time;
    data["token"] = token;
    let query = Object.entries(data).map(x=>x[0]+"="+encodeURIComponent(x[1])).join("&")
    return {
        url: url + "?" + query,
        httpHeaders: {
            sign,
            Fing,
            "User-Agent": "Mozilla/5.0 (Linux; Android 9; PCRT00 Build/PQ3B.190801.002; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/91.0.4472.114 Mobile Safari/537.36"
        },
        cacheTime: 3600

    }
}

