    if (type == 1) {
        /// getvddr2
        url = `${item.src1}|getvddr2`;
    } else if (type == 4) {
        /// 使用src0的地址
        // if (url.slice(-4) == 'm3u8') {
        // } else {
        //     if (url.slice(0x0, 0x5) == 'https') {
        //     }else{
        //     }
        // }
        if (url.slice(0x0, 0x5) !== 'https') {
            url = `https://v.ddys.pro${url}`;
        }
    } else if (type == 2) {
        /// getvddr3
        url = `${item.src1}|getvddr3`;
    }
    list.push({ "title": item.caption, "url": url })
}

return list

