    const media_key = CryptoJS['enc']['Utf8']['parse']('f5d965df75336270');
    const media_iv = CryptoJS['enc']['Utf8']['parse']('97b60394abc2fbe1');
    word = CryptoJS.lib.WordArray.create(word);
    word = CryptoJS.enc.Base64.stringify(word);
    const decrypt = CryptoJS.AES.decrypt(word, media_key, {
        iv: media_iv,
        mode: CryptoJS.mode.CBC,
        padding: CryptoJS.pad.NoPadding,
    });
    let encryptedUint8Array = new Uint8Array(decrypt.sigBytes);
    for (let i = 0; i < decrypt.sigBytes; i++) {
        encryptedUint8Array[i] = decrypt.words[i >>> 2] >>> (24 - (i % 4) * 8) & 0xff;
    }
    return encryptedUint8Array;
}
