const desDecode = function(data,bid) {
    data = JSON.parse(tools.base64Decode(data)); 
    let nonce = data.nonce;
    if (data.cipher && bid && nonce && nonce.length === 5) {
        nonce = (bid.length < 3) ? 
            ((bid.length === 1) ? nonce + bid + "00" : nonce + bid + "0") :
            nonce + bid.substring(bid.length - 3);
        try {
            console.log(nonce)
            const key =CryptoJS.enc.Utf8.parse(nonce);
            const iv = CryptoJS.enc.Base64.parse('AQIDBAUGBwg=');
            let dec = CryptoJS.DES.decrypt(data.cipher, key, {
                iv:iv,
                mode: CryptoJS.mode.CBC,
                padding: CryptoJS.pad.Pkcs7,
             }).toString(CryptoJS.enc.Utf8); 
            if (dec.length > bid.length) {
                return dec.substring(bid.length);
            }
        } catch (error) {
            console.error(error);
        }
    }
    return null;
}

