const generateUUID = () => {
    let d = new Date().getTime()
    let d2 = ((typeof performance !== 'undefined') && performance.now && (performance.now() * 1000)) || 0
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
        let r = Math.random() * 16

        if (d > 0) {
            r = (d + r) % 16 | 0
            d = Math.floor(d / 16)
        } else {
            r = (d2 + r) % 16 | 0
            d2 = Math.floor(d2 / 16)
        }
        return (c === 'x' ? r : (r & 0x3 | 0x8)).toString(16)
    });
}

let api = "https://client-rapi-missav.recombee.com";
let base = "/missav-default/search/users/";
base += generateUUID();
base += "/items/?frontend_timestamp=";
base += parseInt(new Date().getTime() / 1e3);
let sign = CryptoJS.HmacSHA1(base, "Ikkg568nlM51RHvldlPvc2GzZPE9R4XGzaH9Qj4zK9npbbbTly1gj9K4mgRn0QlV").toString();
base += "&frontend_sign=" + sign;
let hp = {
    cascadeCreate: true,
    count: 24,
    includedProperties: [
        "title_cn",
        "duration",
        "has_chinese_subtitle",
        "is_uncensored_leak",
        "dm"
    ],
    returnProperties: true,
    scenario: "search",
    searchQuery: encodeURI(params.keyWord)
};
return {
    "url": api + base,
    "method": "POST",
    "body": hp,
    "headers": {
        "Content-Type": "application/json",
    },
};

