@js:
async function getCdnUrl(params) {
    let cdnUrl = await tools.getCache("m_cdn");
    console.log(cdnUrl);
    if (cdnUrl == null) {
        let resp = await tools.http.get("https://missav.com");
        let cdn = resp.body.match(/\`(.*)?\$\{path\}/)[1];
        console.log(cdn);
        await tools.setCache("m_cdn", cdn);
        cdnUrl = cdn;
    }
    return cdnUrl;
}

let res = await tools.cssSelector(result,"div.thumbnail.group");

if (res.length==0) {
    let list = [];
    let data = JSON.parse(result).recomms;
    await getCdnUrl();

    let cdnUrl = await tools.getCache("m_cdn");

    for (const item of data) {
        let value = item.values;
        let title = value.title_cn;
        list.push({
            "title": item.id.toUpperCase() +" "+ title,
            "url": `/dm${value.dm}/cn/${item.id}`,
            "cover": `${cdnUrl}/${item.id}/cover.jpg?class=thumbnail`,
        });
    }
    return list;
} else {
    return res;
}

