@js:
let videoUrl;
let url = JSON.parse(result).data[0];
let resp = undefined;
// let url = 'https://dood.so/e/obo9vftdx2ep?c_poster=https://sextb.net/images/cover-player.jpg';
if (url.indexOf('streamtape') != -1 || url.indexOf('stapad') != -1) {
    let resp = tools.http.get(url);
    let link = resp.match(/innerHTML.=.[\"\'].*?;/g) ?? [];
    if (link.length == 0) {
        return '解析失败';
    }
    let s = link[link.length - 1] ?? '';
    videoUrl = 'https:' + eval(s.replace(/innerHTML.=./, '')) + '&stream=1';
    let res = tools.httpRequest({ url: videoUrl, forbidRedirect: true });
    let location = res.headers['location'] ?? '';
    // print(`location:${location}`);
    return location;
} else if (url.indexOf('dood.') != -1) {
    let makePlay = (token) => {
        for (var a = "", t = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789", n = t.length, o = 0; 10 > o; o++)
            a += t.charAt(Math.floor(Math.random() * n));
        return `${a}?token=${token}&expiry=${Date.now()}`;
    }
    // print(`url:${url}`);
    let gethost = url.match(/http.*?\.\w+\//g)?.join() ?? '';
    gethost = gethost.replace(/,.*/g, '');
    gethost = gethost.substring(0, gethost.length - 1);
    // print(`gethost:${gethost}`);
    resp = await tools.httpRequest({ url: url, h2: true, headers: { 'referer': url } });
    let link = resp.body.match(/\/pass_md5.*?',/g)?.join() ?? '';

    link = link.substring(0, link.length - 2);
    let res = await tools.httpRequest({ url: `${gethost}${link}`, h2: true, headers: { 'referer': url } });
    // print(res.body);
    // print(`pass_md5_link:${link}`);
    let paramsList = link.match(/\/[\w-]+/g);
    // print(`paramsList:${paramsList}`);
    let hash = paramsList[1] ?? '';
    let token = paramsList[2] ?? '';
    // print(`token:${token}`);
    // print(`hash:${hash}`);
    let startPlayUrl = `${gethost}/dood?op=watch&hash=${hash.substring(1)}&token=${token.substring(1)}&embed=1&ref2=${encodeURIComponent(url)}&adb=1&ftor=0`;
    await tools.httpRequest({ url: startPlayUrl, h2: true, headers: { 'referer': url } });
    // print(`startPlayUrl:${startPlayUrl}`);
    link = res.body + makePlay(token.substring(1));
    // print(`link:${link}`);
    let headers = {};
    headers['Referer'] = gethost + '/';
    headers['Accept-Encoding'] = 'Accept-Encoding: identity;q=1, *;q=0';
    headers['Accept-Language'] = 'Accept-Language: zh-CN,zh;q=0.9,eo;q=0.8,en;q=0.7,ko;q=0.6';
    headers['User-Agent'] = 'Mozilla/5.0 (iPhone; CPU iPhone OS 16_4_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148';
    return `${link},${JSON.stringify(headers)}`;
}
resp = await tools.httpRequest({ "url": url, webview: true, sourceRegex: '(?:\.m3u8|\.mp4|api\/source)' });
let parseUrl = resp.body;
if (parseUrl.indexOf("/api/source") != -1) {
    videoUrl = JSON.parse(await http.post(url.replace("/v/", "/api/source/"), "r=&d=")).data[0].file;;
} else {
    let headers = {};
    headers['Connection'] = 'keep-alive';
    headers['User-Agent'] = 'Mozilla/5.0 (iPhone; CPU iPhone OS 16_4_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148';
    if (url.indexOf("streamsb") != -1) {
        headers['Referer'] = 'https://streamsb.net/';
        headers['Origin'] = 'https://streamsb.net';
    }
    if (url.indexOf("javplayer.me") != -1) {
        headers['Referer'] = 'https://javplayer.me/';
        headers['Origin'] = 'https://javplayer.me';
    }else{
headers['Referer'] = url;
}
    videoUrl = `${parseUrl},${JSON.stringify(headers)}`;
}
return videoUrl;

