function getUrlList(html){
  let list = []
  let {url} = params.queryInfo
  let num = +html.match(/共(\d+)页/)[1]
  for(let i = 2; i <= num; i++){
    list.push(url.replace(/1(?=\.htm)/, i))
  }
  return list
}

async function getAllHtml(urls){
  let promises = urls.map(url=>tools.httpRequest(url))
  let list = await Promise.all(promises)
  let resList = list.map(resp => resp.body)
  return resList.join("")
}

