azure.requestInfo:
@js:

let url="https://tts.xiaoshame1.pp.ua/tts/";
let body={
    voice_id:params.filters.type,
};
let _conf={
      key:'text',
      body:body,
      headers:{},
      post:false,
      url:url
 };

params.nativeTool.setCache("xsreader_voice_conf",JSON.stringify(_conf));
return config.host

azure.bookName:
@js:
let regt=`\n\s*(.*?)::${params.filters.type}\n`
let reg=new RegExp(regt)
let name=config.moreKeys.requestFilters.match(reg)[1]
return "当前语音："+name
