    let larr=list.split(/(\|{2}\s*@js:|^\s*@js:)/);
    let ljs=larr.length==3?larr[2]:"";
    let _list=r.queryWithXPath(larr[0].trim());
    if(ljs){
      let result=_list;
       _list=eval("function test(){"+ljs+"};test()");
     }
    let lists=[];
    ["list","params","config","result"].map(x=>{
         delete opt[x];
    });
    _list.forEach(x=>{
          let tmp={};
          for(let n in opt){
              let y=opt[n].trim();
               if(y){
                 let arr=y.split(/(\|{2}\s*@js:|^\s*@js:)/);
                 let js=arr.length==3?arr[2]:"";
                let cont=x.queryWithXPath(arr[0].trim())
                if(cont.length){
                   cont=cont[0].content()||"";
                }
                if(js){
                   let result=cont;
                   cont=eval("function test(){"+js+"};test()");
                }
                if(cont){
                   tmp[n]=cont;
                }
             }
         }
      if(Object.getOwnPropertyNames(tmp).length){
  lists.push(tmp);
    }
  });
  return lists;
}

